-- Update contract_values table with the new wage system
-- This script updates the def_value and att_value columns

-- First, let's see what we have currently
SELECT * FROM contract_values ORDER BY rating DESC LIMIT 10;

-- Clear existing data
DELETE FROM contract_values;

-- Insert the new wage system data (including ratings 99-96)

-- Verify the updates
SELECT 
  rating,
  def_value,
  att_value,
  (att_value - def_value) as difference
FROM contract_values 
ORDER BY rating DESC 
LIMIT 15; 